T_USERAGENT,'amoCRM-oAuth-client/1.0');curl_setopt($curl,CURLOPT_URL, $link_leads);curl_setopt($curl,CURLOPT_HTTPHEADER, $headers);curl_setopt($curl,CURLOPT_SSL_VERIFYPEER, 1);curl_setopt($curl,CURLOPT_SSL_VERIFYHOST, 2);curl_setopt($curl,CURLOPT_CUSTOMREQUEST,'POST');curl_setopt($curl,CURLOPT_POSTFIELDS,json_encode($leads)); curl_setopt($curl,CURLOPT_HEADER,false);$out=curl_exec($curl); $code=curl_getinfo($curl,CURLINFO_HTTP_CODE); /** * Данные получаем в формате JSON, поэтому, для получения читаемых данных, * нам придётся перевести ответ в формат, понятный PHP */$Response=json_decode($out,true);$Response=$Response['response']['leads']['add']; $outputt=PHP_EOL;foreach($Response as $v)  if(is_array($v))    $outputt.=$v['id'].PHP_EOL;return $outputt;?>